import React from 'react';
import logo from './logo.svg';
import './App.css';

import socketio from 'socket.io-client';
const socket = socketio.connect('http://localhost:8081');

class Form extends React.Component {
	constructor (props) {
		super(props)
		this.state = { name: '', message: '' , votename: 'Biseo'}
	}

	messageChanged (e) {
		this.setState({message: e.target.value})
	}

	nameChanged (e) {
		this.setState({name: e.target.value})
	}

	send() {
		socket.emit('chat message', {
			name: this.state.name,
			message: this.state.message
		})
		this.setState({message: ''})
	}

    vote_send() {
        socket.emit('vote', {
			votename: this.state.votename
        })
        this.setState({votename: ''})
    }

  render () {
    return (
      <div className="chat">
        {this.state.name} : 
        <input value={this.state.message}
          onChange={e => this.messageChanged(e)} />
        <button onClick={e => this.send()}>전송</button>
      </div>
    )
  }

}

class App extends React.Component {
  constructor(props) {
	super(props)
	this.state = { logs: [] }
  }

  componentDidMount() {
	socket.on('chat message', (obj) => {
		const logs2 = this.state.logs
		obj.key = 'key_' + (this.state.logs.length+1)
		console.log(obj)
		logs2.unshift(obj)
		this.setState({logs: logs2})
	})
  }

  render() {
  const messages = this.state.logs.map(e => (
	<div style={{display: 'flex', flexDirection: 'column'}} key={e.key}>
		<span >{e.name} : </span>
		<span >{e.message}</span>
		<p style={{clear: 'both'}} />
	</div>
	))
  const vote = this.state.logs.map(e => (
	<div style={{display: 'flex', flexDirection: 'column'}} key={e.key} >
		<span>{e.votename} <br/></span>
		<span>찬성</span>
		<span>반대</span>
		<p style={{clear: 'both'}} />
	</div>
	))
  return (
	<div>
	<header>
		<div className="full-view">
			<div className="vote-room">
				<h1>Vote room</h1>
				<div>
					{vote}
				</div>
			</div>
			<div className="chat-room">
				<h1>Chatting room</h1>
	    		<div>
	      			<div>
	        			{messages}
	      			</div>
	      			<div>
						<Form />
	      			</div>
				</div>
			</div>
		</div>
	</header>
    </div> 
  );
  }
}

export default App;
