import React from 'react';
import logo from './logo.svg';
import './App.css';

import socketio from 'socket.io-client';
const socket = socketio.connect('http://localhost:8081');

class ChatForm extends React.Component {
	constructor (props) {
		super(props)
		this.state = { name: 'A', message: '' , votename: 'Biseo'}
	}

	messageChanged (e) {
		this.setState({message: e.target.value})
	}

	nameChanged (e) {
		this.setState({name: e.target.value})
	}

	send() {
		socket.emit('chat message', {
			name: this.state.name,
			message: this.state.message
		})
		this.setState({message: ''})
	}

    vote_send() {
        socket.emit('vote', {
			votename: this.state.votename
        })
        this.setState({votename: ''})
    }

  render () {
    return (
      <div className="chat">
      	<div className="chat-box">
			나 :
		</div>  
		<input value={this.state.message} onChange={e => this.messageChanged(e)} />
        <button onClick={e => this.send()}>전송</button>

      </div>
    )
  }
}

class NameForm extends React.Component {
	constructor (props) {
		super(props)
		this.state = { name: ''}
	}

	nameChanged (e) {
		this.setState({name: e.target.value})
	}

	send() {
		socket.emit('name message', {
			name: this.state.name,
		})
		this.setState({name: ''})
	}

  render () {
    return (
      <div className="chat">
      	<div className="chat-box">
			Name : 
		</div>  
		<input value={this.state.name} onChange={e => this.nameChanged(e)} />
        <button onClick={e => this.send()}>변경</button>
      </div>
    )
  }
}

class App extends React.Component {
  constructor(props) {
	super(props)
	this.state = { logs_chat: [], logs_vote: [] }
  }

  componentDidMount() {
	socket.on('chat message', (obj) => {
		const logs2 = this.state.logs_chat
		obj.key = 'key_' + (this.state.logs_chat.length+1)
		console.log(obj)
		logs2.unshift(obj)
		this.setState({logs_chat: logs2})
	})
	socket.on('vote', (obj) => {
		const logs3 = this.state.logs_vote
		obj.key = 'key_' + (this.state.logs_vote.length+1)
		console.log(obj)
		logs3.unshift(obj)
		this.setState({logs_vote: logs3})
	})
  }

  render() {
  const messages = this.state.logs_chat.map(e => (
	<div style={{display: 'flex', flexDirection: 'column'}} key={e.key}>
		<span >{e.name} : </span>
		<span >{e.message}</span>
		<p style={{clear: 'both'}} />
	</div>
	))
  const vote = this.state.logs_vote.map(e => (
	<div style={{display: 'flex', flexDirection: 'column'}} key={e.key} >
		<span>{e.votename} <br/></span>
		<span>찬성</span>
		<span>반대</span>
		<p style={{clear: 'both'}} />
	</div>
	))
  return (
	<div>
	<header>
		<NameForm/>
		<div className="full-view">
			<div className="vote-room">
				<h1>Vote room</h1>
				<div>
					{vote}
				</div>
			</div>
			<div className="chat-room">
				<h1>Chatting room</h1>
	    		<div>
	      			<div>
	        			{messages}
	      			</div>
	      			<div>
						<ChatForm />
	      			</div>
				</div>
			</div>
		</div>
	</header>
    </div> 
  );
  }
}

export default App;
